### Import arcpy module ###
import arcpy

### Custom Functions ###

def Watershed_Builder( dem, Pour_Point_shp ):
    # Process: Fill
    FILLED_DEM = arcpy.sa.Fill( dem )

    # Process: Flow Direction
    FlowDir = arcpy.sa.FlowDirection( FILLED_DEM )
    FILLED_DEM = None

    # Process: Flow Accumulation
    FlowAcc = arcpy.sa.FlowAccumulation( FlowDir )

    # Process: Snap Pour Point
    SnapPP = arcpy.sa.SnapPourPoint( Pour_Point_shp , FlowAcc , 0 )
    FlowAcc = None

    # Process: Watershed
    Watersh_Flow1 = arcpy.sa.Watershed( FlowDir , SnapPP )
    FlowDir = None
    SnapPP = None

    return Watersh_Flow1


